import { useState } from 'react';
import { useRef } from 'react';
import emailjs from '@emailjs/browser';
import "./Contact.css"

export default function Contact() {
    const formRef = useRef();
    const [done, setDone] = useState();

    const handleSubmit = (e)=>{
        e.preventDefault();

        emailjs.sendForm('service_3pcglqg', 'template_s3h8aql', formRef.current, 'user_9gtH5ZYGxHRj7ArAxylqu')
            .then((result) => {
                console.log(result.text);
                setDone(true)
            }, (error) => {
                console.log(error.text);
            });

    }

  return (
      <div className="c">
          <div className="c-bg"></div>
              <div className="c-wrapper">
                  <div className="c-left">
                      <h1 className="c-title">Have a Project? Let's discuss it today</h1>
                      <div className="c-info">
                          <div className="c-info-item">
                              <img src="" alt="phone" className='c-icon'/>
                          </div>
                          <div className="c-info-item">
                              <img src="" alt="" className='c-icon'/>
                              +91 785784570
                          </div>
                          <div className="c-info-item">
                              <img src="" alt="email" className='c-icon'/>
                          </div>
                          <div className="c-info-item">
                              <img src="" alt="address" className='c-icon'/>
                          </div>
                      </div>
                  </div>
                  <div className="c-right">
                      <div className="c-desc">
                          <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium distinctio officia placeat facere. Voluptas, enim! Consectetur provident minima laboriosam eos facere, soluta praesentium illo,
                               amet quo cupiditate blanditiis nemo atque. 
                        </p>
                        <form ref={formRef} onSubmit={handleSubmit}>
                            <input type="text" placeholder="Name" name="user_name" />
                            <input type="text" placeholder="Subject" name="user_subject" />
                            <input type="email" placeholder="Email" name="user_email" />

                            <textarea name="message" placeholder='message' cols="30" rows="10"></textarea>
                            <button>Submit</button>
                            {done && "Thank you, your query has been submitted!"}
                        </form>
                      </div>
                  </div>
              </div>
          
      </div>
  )
}
